INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(1, '鍵掛峠(Kagikake Toge)', '','	鳥取県日野郡江府町大河原鍵１５３１−２９','標高約910mに位置する、大山屈指の絶景ポイント。手前に大山のシンボル・ブナ林の絨毯が広がり、その奥に立ちはだかる南壁の山肌とのコントラストは、神秘すら感じるほどの美しさ。紅葉の名所としても有名で、赤やオレンジ、緑といった色とりどりの光景に圧倒されるはず。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(1, '木谷沢渓流(Kitanizawa Keiryu)','','鳥取県日野郡江府町江尾475','日本百名山の一つ、中国地方最高峰の「大山」の自然の恵みを受け、良質の天然水と美味しい空気が魅力の奥大山を象徴する渓流。耳に届けられるせせらぎの音や小鳥たちの鳴き声などに癒され、目の前に広がる光景は神秘的。' );
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(1, '湖山池(Koyama Ike)', '','	鳥取県鳥取市湖山町南','砂丘の発達によって日本海から分離してできた湖。湖面には大小の島がある。岸から橋で結ばれた公園は、桜の名所だ。語り部が案内する遊覧船も運航。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(1, '大山(Daisen)','','	鳥取県西伯郡大山町大山','日本百名山に名を連ねる標高1709mの山。古くから神聖な山として崇められる。夏山登山、キャンプ、スキーなど四季を通じてアウトドアライフを楽しむ観光客で賑わっている。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(1, '大山トム・ソーヤ牧場(Daisen Tom Soya Bokujyou)','','	鳥取県米子市岡成６２２－２','「めぇーめぇーランド」は14種、約160頭の動物、「わんわんランド」は約40頭のイヌとふれあえる。ポニーの乗馬、ヤギの搾乳体験も楽しめる。アルパカの散歩タイムも必見。' );
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(1, '中国庭園燕趙園(Chugoku Teien Enchoen)', '','鳥取県東伯郡湯梨浜町引地５６５－１','鳥取県と中国河北省の友好のシンボルとして建設された国内最大級の本格的中国庭園。設計から資材の調達・加工まで、すべて中国で行われた。毎日開催の中国雑技ショーは必見。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(1, '鳥取砂丘(Tottori Sakyu)','','鳥取県鳥取市福部町湯山','東西約16km、南北約2.4kmに広がり、そのうち観光地となっている砂丘は、天然記念物にも指定されている。メインの入り口は砂丘会館向かいにあり、周辺にはレストハウスやみやげ店がそろっている。' );
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(1, '鳥取城跡(Tottorijyo Ato)','','鳥取県鳥取市東町','鳥取市街を一望する久松山にある城跡。現在は石垣や櫓、内堀跡などが残っている。周囲は、桜の名所で知られる久松公園として整備されている。' );
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(1, '白兎海岸(Hakuto Kaigan)','','鳥取県鳥取市白兎','神話「因幡の白兎」の舞台として知られる白兎海岸は、日本の渚百選のひとつ。水が綺麗な浜としても知られ、夏場には多くの海水浴客で賑わう。' );
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(1, 'はわい温泉(Hawai Onsen)', '','鳥取県東伯郡湯梨浜町はわい温泉','明治35年開湯の温泉街。名物は「湖上温泉」で、湖にせり出すように温泉宿が建ち、露天風呂では東郷湖の絶景を眺めながら温泉を楽しめる。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(1, '桝水高原(Masumizu Kougen)','','鳥取県西伯郡伯耆町岩立桝水高原','大山西側の山麓に広がる「大山桝水高原」。天空リフトに乗って標高900ｍの山頂付近へ登ると、日本海、弓ヶ浜半島、島根半島の大パノラマが一望できるほか、レストランでバーベキューランチなどを楽しむことができる。また、ロマンチックな絶景が広がることからプロポーズの場所に選ばれることも多く、「恋人の聖地」としても人気。' );
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(1, '三朝温泉(Misasa Onsen)','','鳥取県東伯郡三朝町三朝','源義朝の家来が白狼に導かれて発見したと伝わる。三朝の名は、三晩泊まって三回朝を迎えると、どんな難病も治るというところから。ラドンを多量に含む湯は肌ざわりがよく効能の良さで知られる。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(2, '稲佐の浜(Inasa no Hama)', '','島根県出雲市大社町杵築北','全国的には神無月と呼ばれる旧暦の10月だが、出雲では神有月と呼び、全国の神々が出雲に集まるという。神々はこの浜から出雲に上陸すると伝えられ、旧暦の10月10日には神迎神事が行われるパワースポットとして知られる。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(2, '出雲大社(Izumo Taisha)', '','島根県出雲市大社町杵築東１９５','縁結びの神として名高い大国主大神を祀り、年間約200万人もの参拝者が訪れる神社。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(2, 'しまね海洋館アクアス(Shimane Kaiyoukan Akuasu)','','	島根県浜田市久代町１１１７－２','美しい海岸線が続く島根県立石見海浜公園内にある水族館。海底トンネルや逆L字型水槽、タッチプールなど、工夫を凝らした展示で世界の海からやってきた生物とふれあえる。' );
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(2, '宍道湖自然館ゴビウス(Shinjiko sjizenkan Gobiusu)', '','島根県出雲市園町１６５９－５','島根県の河川や宍道湖、中海に暮らす生き物、約200種10000点を展示する。子供から大人まで楽しめる水族館。土・日曜には解説を交えて魚が餌を食べる様子を紹介している。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(2, '宍道湖夕日スポット(Shinjiko Yuhi Spot)', '','島根県松江市袖師町','小泉八雲や田山花袋など多くの文豪に愛された宍道湖の夕景の美しさは絶景。日本夕陽百選にも選定され「水の都松江」の象徴。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(2, '玉造温泉(Tamatsukuri Onsen)', '','島根県松江市玉湯町玉造','『出雲国風土記』にも登場、平安時代には清少納言が『枕草子』で名湯とたたえた。古代、特産のメノウで勾玉を作る玉作部の里だったことが名の由来。温泉街の中央を宍道湖に注ぐ玉湯川が流れる。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(2, '壇鏡の滝(Dangyou no Taki)', '','島根県隠岐郡隠岐の島町那久','壇鏡神社社殿両側にある高さ約40mに及ぶ2本の滝。向かって右側の滝が雄滝、左側が雌滝。昔から闘牛大会参加者の清めの水、長寿の水としても重宝され「名水百選」にも選ばれている。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(2, '日本庭園由志園(Nihonteien Yushien)', '','島根県松江市八束町波入１２６０－２','大輪のボタンをはじめ、四季折々の花や自然が観賞できる1万坪の回遊式日本庭園。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(2, '日御碕神社(Hinomisaki Jinjya)', '','島根県出雲市大社町日御碕４５５','社殿は、徳川三代将軍の家光によって着工以来10年もの歳月をかけて建てられた権現造り。国の重要文化財に指定されている。');
INSERT INTO  spots(pref_id, name, img, location, outline) VALUES(2, '松江フォーゲルパーク(Matsue Vogelpark)', '','島根県松江市大垣町５２','世界でも珍しい花と鳥の公園。国内最大級の規模を誇る室内ガーデンに年中満開の約1400品種のベゴニア、フクシアを中心とする花がある。園内各所で90種400羽の熱帯の鳥や水鳥などと出会える。');
